import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';
import './Navigation.css';


const Navigation = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { isDarkMode, toggleTheme } = useTheme();
  const location = useLocation();

  const navItems = [
    { 
      path: '/', 
      icon: (
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
          <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          <polyline points="9,22 9,12 15,12 15,22" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      )
    },
    { 
      path: '/about', 
      icon: (
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
          <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          <circle cx="12" cy="7" r="4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      )
    },
    { 
      path: '/work', 
      icon: (
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
          <rect x="3" y="3" width="7" height="7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          <rect x="14" y="3" width="7" height="7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          <rect x="14" y="14" width="7" height="7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          <rect x="3" y="14" width="7" height="7" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      )
    },
    { 
      path: '/contact', 
      icon: (
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
          <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          <polyline points="22,6 12,13 2,6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      )
    }
  ];

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
    document.body.style.overflow = !isMenuOpen ? 'hidden' : 'auto';
  };

  const handleLinkClick = () => {
    setIsMenuOpen(false);
    document.body.style.overflow = 'auto';
  };

  return (
    <header className="navigation">
      <div className="nav-container">
        {/* Creative Logo with Animation */}
        <div className="nav-left">
          <Link to="/" className="nav-logo">
            <div className="logo-container">
              <div className="logo-circle">
                <span className="logo-initial">H</span>
                <div className="logo-pulse"></div>
              </div>
              <div className="logo-text-container">
                <span className="logo-text">Harsh</span>
                <span className="logo-subtitle">Portfolio</span>
              </div>
            </div>
          </Link>
        </div>

        {/* Creative Navigation with Floating Effect */}
        <div className="nav-center">
          <div className="nav-floating-container">
            <div className="nav-background-blur"></div>
            <div className="nav-items-wrapper">
              {navItems.map((item, index) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`nav-item ${location.pathname === item.path ? 'nav-item-active' : ''}`}
                  onClick={handleLinkClick}
                >
                  <div className="nav-item-content">
                    <div className="nav-item-icon">{item.icon}</div>
                    <span className="nav-item-label">
                      {item.path === '/' ? 'Home' : 
                       item.path === '/about' ? 'About' :
                       item.path === '/work' ? 'Work' : 'Contact'}
                    </span>
                  </div>
                  <div className="nav-item-indicator"></div>
                </Link>
              ))}

              <div className="nav-separator"></div>

              <button
                className="nav-item theme-toggle-btn"
                onClick={toggleTheme}
                aria-label="Toggle theme"
              >
                <div className="nav-item-content">
                  <div className="nav-item-icon theme-icon">
                    {isDarkMode ? (
                      <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
                        <circle cx="12" cy="12" r="4" stroke="currentColor" strokeWidth="2"/>
                        <path d="m12 2 0 2m0 16 0 2m10-10-2 0M4 12l-2 0m15.314-6.314-1.414 1.414M7.1 16.9l-1.414 1.414m12.728 0-1.414-1.414M7.1 7.1 5.686 5.686" stroke="currentColor" strokeWidth="2"/>
                      </svg>
                    ) : (
                      <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
                        <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z" stroke="currentColor" strokeWidth="2"/>
                      </svg>
                    )}
                  </div>
                  <span className="nav-item-label">Theme</span>
                </div>
              </button>
            </div>
          </div>
        </div>

        {/* Creative Status Indicator */}
        <div className="nav-right">
          <div className="status-indicator">
            <div className="status-dot"></div>
            <span className="status-text">Available</span>
          </div>
        </div>

        {/* Enhanced Mobile Menu Button */}
        <button
          className={`mobile-menu-button ${isMenuOpen ? 'mobile-menu-button-active' : ''}`}
          onClick={handleMenuToggle}
          aria-label="Toggle menu"
        >
          <div className="hamburger-lines">
            <span className="line line1"></span>
            <span className="line line2"></span>
            <span className="line line3"></span>
          </div>
        </button>
      </div>

      {/* Enhanced Mobile Menu */}
      <div className={`mobile-menu ${isMenuOpen ? 'mobile-menu-active' : ''}`}>
        <div className="mobile-menu-overlay" onClick={handleMenuToggle}></div>
        <div className="mobile-menu-content">
          <div className="mobile-menu-header">
            <div className="mobile-logo">
              <div className="mobile-logo-circle">H</div>
              <span>Navigation</span>
            </div>
            <button className="mobile-menu-close" onClick={handleMenuToggle}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="2"/>
              </svg>
            </button>
          </div>
          <div className="mobile-menu-links">
            {navItems.map((item, index) => (
              <Link
                key={item.path}
                to={item.path}
                className={`mobile-nav-link ${location.pathname === item.path ? 'mobile-nav-link-active' : ''}`}
                onClick={handleLinkClick}
                style={{ '--delay': `${index * 0.1}s` }}
              >
                <div className="mobile-nav-icon">{item.icon}</div>
                <span className="mobile-nav-text">
                  {item.path === '/' ? 'Home' : 
                   item.path === '/about' ? 'About' :
                   item.path === '/work' ? 'Work' : 'Contact'}
                </span>
                <div className="mobile-nav-arrow">
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                    <path d="M9 18l6-6-6-6" stroke="currentColor" strokeWidth="2"/>
                  </svg>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Navigation;
