import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';
import './Navigation.css';

const Navigation = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeHover, setActiveHover] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const { isDarkMode, toggleTheme } = useTheme();
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'Home', icon: 'ðŸ ' },
    { path: '/work', label: 'Work', icon: 'ðŸ’¼' },
    { path: '/about', label: 'About', icon: 'ðŸ‘¨â€ðŸ’»' },
    { path: '/contact', label: 'Contact', icon: 'ðŸ“§' }
  ];

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);

    // Add loading animation delay
    setTimeout(() => setIsLoaded(true), 100);

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
    // Prevent body scroll when menu is open
    document.body.style.overflow = !isMenuOpen ? 'hidden' : 'auto';
  };

  const handleLinkClick = () => {
    setIsMenuOpen(false);
    document.body.style.overflow = 'auto';
  };

  return (
    <nav className={`navigation ${isScrolled ? 'navigation-scrolled' : ''} ${isLoaded ? 'navigation-loaded' : ''}`}>
      <div className="nav-container">
        <Link to="/" className="nav-logo" onClick={handleLinkClick}>
          <div className="logo-container">
            <div className="logo-initial">
              <span className="logo-letter">H</span>
              <div className="logo-glow"></div>
            </div>
            <span className="logo-text">arsh</span>
            <div className="logo-dot"></div>
          </div>
        </Link>

        <div className="nav-links-container">
          {navItems.map((item, index) => (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-link ${location.pathname === item.path ? 'nav-link-active' : ''}`}
              onMouseEnter={() => setActiveHover(index)}
              onMouseLeave={() => setActiveHover(null)}
              style={{ '--delay': `${index * 0.1}s` }}
            >
              <span className="nav-link-icon">{item.icon}</span>
              <span className="nav-link-text">{item.label}</span>
              <div className="nav-link-indicator"></div>
              <div className="nav-link-ripple"></div>
            </Link>
          ))}
          <div 
            className="nav-links-background"
            style={{
              transform: activeHover !== null ? `translateX(${activeHover * 100}%)` : 'translateX(-100%)',
              opacity: activeHover !== null ? 1 : 0
            }}
          ></div>
        </div>

        <div className="nav-actions">
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            aria-label="Toggle theme"
          >
            <div className="theme-icon">
              <div className="theme-icon-inner">
                {isDarkMode ? (
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
                    <circle cx="12" cy="12" r="4" stroke="currentColor" strokeWidth="2"/>
                    <path d="m12 2 0 2m0 16 0 2m10-10-2 0M4 12l-2 0m15.314-6.314-1.414 1.414M7.1 16.9l-1.414 1.414m12.728 0-1.414-1.414M7.1 7.1 5.686 5.686" stroke="currentColor" strokeWidth="2"/>
                  </svg>
                ) : (
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
                    <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z" stroke="currentColor" strokeWidth="2"/>
                  </svg>
                )}
              </div>
            </div>
            <div className="theme-toggle-ripple"></div>
          </button>

          <button
            className={`nav-hamburger ${isMenuOpen ? 'nav-hamburger-active' : ''}`}
            onClick={handleMenuToggle}
            aria-label="Toggle menu"
          >
            <div className="hamburger-box">
              <div className="hamburger-inner">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
            <div className="hamburger-ripple"></div>
          </button>
        </div>

        <div className={`mobile-menu ${isMenuOpen ? 'mobile-menu-active' : ''}`}>
          <div className="mobile-menu-overlay" onClick={handleMenuToggle}></div>
          <div className="mobile-menu-content">
            <div className="mobile-menu-header">
              <h3>Navigation</h3>
              <button className="mobile-menu-close" onClick={handleMenuToggle}>
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                  <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="2"/>
                </svg>
              </button>
            </div>
            <div className="mobile-menu-links">
              {navItems.map((item, index) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`mobile-nav-link ${location.pathname === item.path ? 'mobile-nav-link-active' : ''}`}
                  onClick={handleLinkClick}
                  style={{ '--delay': `${index * 0.1}s` }}
                >
                  <span className="mobile-nav-icon">{item.icon}</span>
                  <span className="mobile-nav-text">{item.label}</span>
                  <div className="mobile-nav-arrow">
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                      <path d="M9 18l6-6-6-6" stroke="currentColor" strokeWidth="2"/>
                    </svg>
                  </div>
                </Link>
              ))}
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
